<?phpnamespace Maras0830\TwitchApi\Core;use GuzzleHttp\Client;/** * Class Chat * @package Maras0830\TwitchApi\Core */class Chat extends Base{    /**     * Returns a links object to all other chat endpoints.     *     * @param $channel     * @return mixed     */    public function chatChannel($channel)    {        $response = $this->client->request('GET', 'kraken/chat/' . $channel);        return json_decode($response->getBody()->getContents(), true);    }    /**     * Returns a list of all emoticon objects for Twitch.     *     * @param $channel     * @return mixed     */    public function chatBadges($channel)    {        $response = $this->client->request('GET', 'kraken/chat/' . $channel . '/badges');        return json_decode($response->getBody()->getContents(), true);    }    /**     * Returns a list of all emoticon objects for Twitch.     *     * @return mixed     */    public function chatEmoticons()    {        $response = $this->client->request('GET', '/kraken/chat/emoticons');        return json_decode($response->getBody()->getContents(), true);    }    /**     * Returns a list of emoticons.     *     * @return mixed     */    public function chatEmoticonImages()    {        $response = $this->client->request('GET', '/kraken/chat/emoticon_images');        return json_decode($response->getBody()->getContents(), true);    }}