<?phpnamespace Maras0830\TwitchApi\Core;class Videos extends Base{    /**     * Get video object.     *     * @param $id     * @return mixed     */    public function video($id)    {        $response = $this->client->get('/kraken/videos/' . $id);        return $response->json();    }    /**     * Get top videos by number of views,     *     * @param array $options     * @return mixed     */    public function getTopVideos($options = [])    {        $defaultOptions = ['limit', 'offset', 'game', 'period'];        $query = [];        foreach ($defaultOptions as $option) {            if (isset($options[ $option ])) {                $query[ $option ] = $options[ $option ];            }        }        $parameters = $this->getDefaultHeaders();        $parameters[ 'query' ] = $query;        $response = $this->client->get('/kraken/videos/top', $parameters);        return $response->json();    }    /**     * Get list of video objects belonging to channel.     *     * @param $channel     * @param null $options     * @return mixed     */    public function getVideosByChannel($channel, $options = null)    {        $defaultOptions = ['limit', 'offset', 'broadcasts', 'hls'];        $query = [];        foreach ($defaultOptions as $option) {            if (isset($options[ $option ])) {                $query[ $option ] = $options[ $option ];            }        }        $parameters = $this->getDefaultHeaders();        $parameters[ 'query' ] = $query;        $response = $this->client->get('/kraken/channels/' . $channel . '/videos', $parameters);        return $response->json();    }}